variantSystem.example:
// button.variants.ts
import { cva } from 'class-variance-authority';

export const buttonVariants = cva(base, {
  variants: {
    variant: { contained: '...', outlined: '...', text: '...' },
    size: { sm: '...', md: '...', lg: '...' }
  },
  defaultVariants: { variant: 'contained', size: 'md' }
});
